import React, { useState, useEffect } from "react";
import moment from "moment-timezone";
import Datetime from "react-datetime";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCalendarAlt } from '@fortawesome/free-solid-svg-icons';
import { Col, Row, Card, Form, Button, InputGroup } from '@themesberg/react-bootstrap';


export const GeneralInfoForm = () => {
  const [userData, setUserData] = useState({
    lastName: '',
    firstName: '',
    email: '',
    role: '',
    language: '',
    locale: '',
    status: ''
  });

  useEffect(() => {
    setUserData({
      lastName: localStorage.getItem('lastName') || '',
      firstName: localStorage.getItem('firstName') || '',
      email: localStorage.getItem('email') || '',
      role: localStorage.getItem('role') || '',
      language: localStorage.getItem('language') || '',
      locale: localStorage.getItem('locale') || '',
      status: localStorage.getItem('status') || ''
    });
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserData({
      ...userData,
      [name]: value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Sauvegarde des données dans le stockage local
    localStorage.setItem('lastName', userData.lastName);
    localStorage.setItem('firstName', userData.firstName);
    localStorage.setItem('email', userData.email);
    localStorage.setItem('role', userData.role);
    localStorage.setItem('language', userData.language);
    localStorage.setItem('locale', userData.locale);
    localStorage.setItem('status', userData.status);
    // Envoi des données au backend ou effectuer d'autres actions de sauvegarde
  };

  return (
    <Card border="light" className="bg-white shadow-sm mb-4">
      <Card.Body>
        <h5 className="mb-4">General information</h5>
        <Form onSubmit={handleSubmit}>
          <Row>
            <Col md={6} className="mb-3">
              <Form.Group id="firstName">
                <Form.Label>First Name</Form.Label>
                <Form.Control 
                  required 
                  type="text" 
                  placeholder="Enter your first name" 
                  name="firstName"
                  value={userData.firstName}
                  onChange={handleInputChange}
                />
              </Form.Group>
            </Col>
            <Col md={6} className="mb-3">
              <Form.Group id="lastName">
                <Form.Label>Last Name</Form.Label>
                <Form.Control 
                  required 
                  type="text" 
                  placeholder="Also your last name" 
                  name="lastName"
                  value={userData.lastName}
                  onChange={handleInputChange}
                />
              </Form.Group>
            </Col>
          </Row>

          <Row>
            <Col md={6} className="mb-3">
              <Form.Group id="email">
                <Form.Label>Email</Form.Label>
                <Form.Control 
                  required 
                  type="email" 
                  placeholder="name@company.com" 
                  name="email"
                  value={userData.email}
                  onChange={handleInputChange}
                />
              </Form.Group>
            </Col>
            <Col md={6} className="mb-3">
              <Form.Group id="role">
                <Form.Label>Role</Form.Label>
                <Form.Control 
                  required 
                  type="text" 
                  placeholder="Enter your role" 
                  name="role"
                  value={userData.role}
                  onChange={handleInputChange}
                />
              </Form.Group>
            </Col>
          </Row>

          <Row>
            <Col md={6} className="mb-3">
              <Form.Group id="language">
                <Form.Label>Language</Form.Label>
                <Form.Control 
                  required 
                  type="text" 
                  placeholder="Enter your language" 
                  name="language"
                  value={userData.language}
                  onChange={handleInputChange}
                />
              </Form.Group>
            </Col>
            <Col md={6} className="mb-3">
              <Form.Group id="locale">
                <Form.Label>Locale</Form.Label>
                <Form.Control 
                  required 
                  type="text" 
                  placeholder="Enter your locale" 
                  name="locale"
                  value={userData.locale}
                  onChange={handleInputChange}
                />
              </Form.Group>
            </Col>
          </Row>

          <Row>
            <Col md={6} className="mb-3">
              <Form.Group id="status">
                <Form.Label>Status</Form.Label>
                <Form.Control 
                  required 
                  type="text" 
                  placeholder="Enter your status" 
                  name="status"
                  value={userData.status}
                  onChange={handleInputChange}
                />
              </Form.Group>
            </Col>
          </Row>

          <div className="mt-3">
            <Button variant="primary" type="submit">Save All</Button>
          </div>
        </Form>
      </Card.Body>
    </Card>
  );
};
